def test_get_alt_sequence_single_exon_mutation_pos_strand(wb_variant_gk803418, wb_cds_c42d8_1_1) -> None:

    ref_sequence = wb_cds_c42d8_1_1.get_sequence()
    alt_seq_info = wb_cds_c42d8_1_1.get_alt_sequence(variants=[wb_variant_gk803418])
    alt_sequence = alt_seq_info['sequence']
    variant_ref_rel_start = 507
    variant_ref_rel_end = 507
    variant_alt_rel_start = variant_ref_rel_start
    variant_alt_rel_end = variant_ref_rel_end

    # Sequence before variant must be identical
    assert ref_sequence[0:variant_ref_rel_start] == alt_sequence[0:variant_alt_rel_start]
    # Sequence at variant position must match expected ref/alt sequence
    assert ref_sequence[variant_ref_rel_start:variant_ref_rel_end + 1] == wb_variant_gk803418.genomic_ref_seq
    assert alt_sequence[variant_alt_rel_start:variant_alt_rel_end + 1] == wb_variant_gk803418.genomic_alt_seq
    # Sequence after variant must be identical
    assert ref_sequence[variant_ref_rel_end + 1:] == alt_sequence[variant_alt_rel_end + 1:]
    # Alt seq should have one embedded variant
    assert len(alt_seq_info['embedded_variants']) == 1
    embedded_variant = alt_seq_info['embedded_variants'][0]
    # Alt seq variant should be positioned correctly
    # note: AltSeqEmbeddedVariant stores 1-based relative positions
    assert embedded_variant['rel_start'] == variant_alt_rel_start + 1
    assert embedded_variant['rel_end'] == variant_alt_rel_end + 1
    assert embedded_variant['variant'].variant_id == wb_variant_gk803418.variant_id


