def test_get_alt_sequence_inframe_only(wb_cds_c42d8_1_1) -> None:
    # ATCCTTCT > ATCCAAATTCT
    insertion = Variant(variant_id='custom_c42d8.1.1_insertion_variant', genomic_ref_seq='', genomic_alt_seq='AA',
                        seq_id='X', start=5110816, end=5110817)
    variants_alt_phase0_rel_start = 373
    variants_alt_phase0_rel_end = 374

    ref_sequence = wb_cds_c42d8_1_1.get_sequence()
    alt_seq_info = wb_cds_c42d8_1_1.get_alt_sequence(variants=[insertion])
    alt_sequence = alt_seq_info['sequence']
    alt_inframe_seq_info = wb_cds_c42d8_1_1.get_alt_sequence(variants=[insertion], inframe_only=True)
    alt_inframe_sequence = alt_inframe_seq_info['sequence']

    # Phase 0 alt sequence
    assert ref_sequence == ''
    assert alt_sequence == ''
    assert alt_inframe_sequence == alt_sequence[0:-2]
    # Alt seq should have one embedded variant
    assert len(alt_seq_info['embedded_variants']) == 1
    embedded_variant = alt_seq_info['embedded_variants'][0]
    # Alt seq variant should be positioned correctly
    # note: AltSeqEmbeddedVariant stores 1-based relative positions
    assert embedded_variant['rel_start'] == variants_alt_phase0_rel_start + 1
    assert embedded_variant['rel_end'] == variants_alt_phase0_rel_end + 1
    assert embedded_variant['variant'].variant_id == insertion.variant_id

    phase_2_region = wb_cds_c42d8_1_1.sub_region(rel_start=2, rel_end=wb_cds_c42d8_1_1.seq_length)
    phase_2_ref_sequence = phase_2_region.get_sequence()
    phase_2_alt_seq_info = phase_2_region.get_alt_sequence(variants=[insertion])
    phase_2_alt_sequence = phase_2_alt_seq_info['sequence']
    phase_2_alt_inframe_seq_info = phase_2_region.get_alt_sequence(variants=[insertion], inframe_only=True)
    phase_2_alt_inframe_sequence = phase_2_alt_inframe_seq_info['sequence']

    # Phase 2 alt sequence
    assert phase_2_ref_sequence == ''
    assert phase_2_alt_sequence == ''
    assert phase_2_alt_inframe_sequence == phase_2_alt_sequence[2:-2]
    # Alt seq should have one embedded variant
    assert len(phase_2_alt_inframe_seq_info['embedded_variants']) == 1
    embedded_variant = phase_2_alt_inframe_seq_info['embedded_variants'][0]
    # Alt seq variant should be positioned correctly (phase 2 inframe-only results in all positions shifted three bases backwards)
    # note: AltSeqEmbeddedVariant stores 1-based relative positions
    assert embedded_variant['rel_start'] == variants_alt_phase0_rel_start - 3 + 1
    assert embedded_variant['rel_end'] == variants_alt_phase0_rel_end - 3 + 1
    assert embedded_variant['variant'].variant_id == insertion.variant_id


def test_get_alt_sequence_unmasked(wb_cds_c42d8_1_1, wb_variant_gk803418) -> None:
    ref_sequence = wb_cds_c42d8_1_1.get_sequence()
    alt_seq_info = wb_cds_c42d8_1_1.get_alt_sequence(variants=[wb_variant_gk803418])
    alt_sequence = alt_seq_info['sequence']
    alt_unmasked_seq_info = wb_cds_c42d8_1_1.get_alt_sequence(variants=[wb_variant_gk803418], unmasked=True)
    alt_unmasked_sequence = alt_unmasked_seq_info['sequence']

    assert ref_sequence != alt_sequence
    assert alt_sequence != alt_unmasked_sequence
    assert alt_sequence.upper() == alt_unmasked_sequence

